import { useState, useEffect } from "react";
import { TextField, Button, Container, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Navbar from "../../AdminNavbar/Navbar";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(4),
  },
  form: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  textField: {
    margin: theme.spacing(1),
    width: "25ch",
  },
  addButton: {
    marginTop: theme.spacing(2),
  },
}));

function AddJobs() {
  const classes = useStyles();
  const navigate = useNavigate();
  const [jobDetails, setJobDetails] = useState({
    Company_Name: "",
    Title: "",
    Description: "",
    Salary: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setJobDetails((prevJobDetails) => ({
      ...prevJobDetails,
      [name]: value,
    }));
  };

  const handleAddJob = () => {
    const { Company_Name, Title, Description, Salary } = jobDetails;

    if (!Company_Name || !Title || !Description || !Salary) {
      alert("Please fill in all fields.");
      return;
    }

    axios
      .post("http://localhost:8000/create/job", jobDetails)
      .then(function (response) {
        alert("Job Added Successfully!");
        setJobDetails({
          Company_Name: "",
          Title: "",
          Description: "",
          Salary: "",
        });
      })
      .catch(function (error) {
        console.error("Error adding data: " + error.message);
      });
  };

  useEffect(() => {
    const token = sessionStorage.getItem("token");

    axios
      .get("http://localhost:8000/user/checkSession", {
        headers: {
          Authorization: "Bearer " + token,
          "User-Type": "Admin",
        },
      })
      .then((res) => {
        if (!res.data.valid) {
          navigate("/");
        } else if (!res.data.userMatch) {
          navigate("/employeeForbidden");
        }
      });
  });

  return (
    <div>
      <Navbar title="addJobs" />
      <Container className={classes.container}>
        <div className={classes.form}>
          <Typography variant="h5" gutterBottom>
            Enter Details
          </Typography>
          <TextField
            className={classes.textField}
            label="Company Name"
            name="Company_Name"
            value={jobDetails.Company_Name}
            onChange={handleInputChange}
          />
          <TextField
            className={classes.textField}
            label="Job Title"
            name="Title"
            value={jobDetails.Title}
            onChange={handleInputChange}
          />
          <TextField
            className={classes.textField}
            label="Salary"
            name="Salary"
            value={jobDetails.Salary}
            onChange={handleInputChange}
          />
          <TextField
            className={classes.textField}
            label="Job Description"
            name="Description"
            value={jobDetails.Description}
            onChange={handleInputChange}
          />
          <Button
            className={classes.addButton}
            variant="contained"
            color="primary"
            onClick={handleAddJob}
          >
            Add
          </Button>
        </div>
      </Container>
    </div>
  );
}

export default AddJobs;
