// App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Footer from './pages/Footer/Footer';
import Home from './pages/Home';
import About from './pages/About';
import Contact from './pages/Contact';
import Jobs from './pages/Jobs';
import CompanyShowcase from './pages/CompanyShowcase';
import Admin from './pages/Admin';
import Login from './pages/Login';

const PrivateRoute = ({ element: Component, isAdmin, ...rest }) => {
    const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
    const userType = useSelector((state) => state.auth.userType);

    return (
        <Route
            {...rest}
            element={
                isLoggedIn ? (
                    isAdmin && userType === 'admin' ? (
                        <Component />
                    ) : (
                        <Navigate to="/home" />
                    )
                ) : (
                    <Navigate to="/" />
                )
            }
        />
    );
};

const App = () => {
    const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);

    return (
        <div className="App">
            <Router>
                <Navbar bg="primary" variant="dark">
                    <Container>
                        <Navbar.Brand href="/">Job website</Navbar.Brand>
                        <Nav className="me-auto">
                            <Nav.Link href="/home">Home</Nav.Link>
                            <Nav.Link href="/about">About</Nav.Link>
                            <Nav.Link href="/contact">Contact</Nav.Link>
                            <Nav.Link href="/jobs">Jobs</Nav.Link>
                            <Nav.Link href="/companies">Company Showcase</Nav.Link>
                            <Nav.Link href="/admin">Admin</Nav.Link>
                        </Nav>
                    </Container>
                </Navbar>
                <Routes>
                    <Route path="/" element={<Login />} />
                    <PrivateRoute path="/home" element={<Home />} />
                    <PrivateRoute path="/about" element={<About />} />
                    <PrivateRoute path="/contact" element={<Contact />} />
                    <PrivateRoute path="/jobs" element={<Jobs />} />
                    <PrivateRoute path="/companies" element={<CompanyShowcase />} />
                    <PrivateRoute path="/admin" element={<Admin />} isAdmin={true} />
                </Routes>
                {isLoggedIn && <hr />}
                {isLoggedIn && <Footer />}
            </Router>
        </div>
    );
};

export default App;
