// authReducer.js

const initialState = {
    user: null,
    error: null,
  };

  const authReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          user: action.payload,
          error: null,
        };
      case 'LOGIN_FAILURE':
        return {
          ...state,
          user: null,
          error: action.payload,
        };
      // Other cases for different actions
      default:
        return state;
    }
  };

  export default authReducer;
